       const resolvers = {
  Query: {
    client: async (_, { id }) => {
      const clients = await clientRepository.read();
      return clients.find((client) => client.id === id);
    },
    clients: async (_, args) => {
      const { take = 10, skip = 0 } = args.options || {};
      /**
       * @type {Array <*>}
       */
      const clients = await clientRepository.read();

      return {
        items: clients.slice(skip, skip + take),
        totalItems: clients.length,
      };
    },
  },
};
